import React, { useEffect, useState } from 'react';
import { supabase } from './supabaseClient';
import './Calendar.css';

function getDaysInMonth(year, month) {
  return new Date(year, month + 1, 0).getDate();
}

function getFirstDayOfWeek(year, month) {
  return new Date(year, month, 1).getDay();
}

const WEEKDAYS = ['So', 'Mo', 'Di', 'Mi', 'Do', 'Fr', 'Sa'];

export default function Calendar({ user }) {
  const today = new Date();
  const [currentMonth, setCurrentMonth] = useState(today.getMonth());
  const [currentYear, setCurrentYear] = useState(today.getFullYear());
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showPersonal, setShowPersonal] = useState(false);
  const [form, setForm] = useState({ title: '', date: '', persons: '' });
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    fetchEvents();
    // eslint-disable-next-line
  }, [currentMonth, currentYear]);

  async function fetchEvents() {
    setLoading(true);
    const monthStr = String(currentMonth + 1).padStart(2, '0');
    const from = `${currentYear}-${monthStr}-01`;
    const to = `${currentYear}-${monthStr}-${getDaysInMonth(currentYear, currentMonth)}`;
    const { data, error } = await supabase
      .from('events')
      .select('*')
      .gte('date', from)
      .lte('date', to)
      .order('date', { ascending: true });
    if (!error) setEvents(data);
    setLoading(false);
  }

  async function handleAddEvent(e) {
    e.preventDefault();
    if (!form.title || !form.date || !form.persons) return;
    setSaving(true);
    const personsArr = form.persons.split(',').map(p => p.trim());
    const { error } = await supabase.from('events').insert([
      { title: form.title, date: form.date, persons: personsArr }
    ]);
    setSaving(false);
    if (!error) {
      setForm({ title: '', date: '', persons: '' });
      fetchEvents();
    }
  }

  function renderDays() {
    const daysInMonth = getDaysInMonth(currentYear, currentMonth);
    const firstDay = getFirstDayOfWeek(currentYear, currentMonth);
    const days = [];
    for (let i = 0; i < firstDay; i++) {
      days.push(<div key={"empty-"+i} className="calendar-day empty"></div>);
    }
    for (let d = 1; d <= daysInMonth; d++) {
      const dateStr = `${currentYear}-${String(currentMonth + 1).padStart(2, '0')}-${String(d).padStart(2, '0')}`;
      const dayEvents = events.filter(ev => ev.date === dateStr && (!showPersonal || ev.persons.includes(user.name)));
      days.push(
        <div key={d} className={"calendar-day" + (dateStr === today.toISOString().slice(0,10) ? " today" : "") }>
          <div className="calendar-date">{d}</div>
          <ul className="calendar-events">
            {dayEvents.map(ev => (
              <li key={ev.id || ev.title+ev.date} className={ev.persons.includes(user.name) ? 'personal' : ''}>
                {ev.title}
                <span className="calendar-persons">({ev.persons.join(', ')})</span>
              </li>
            ))}
          </ul>
        </div>
      );
    }
    return days;
  }

  function handleMonthChange(offset) {
    let newMonth = currentMonth + offset;
    let newYear = currentYear;
    if (newMonth < 0) {
      newMonth = 11;
      newYear--;
    } else if (newMonth > 11) {
      newMonth = 0;
      newYear++;
    }
    setCurrentMonth(newMonth);
    setCurrentYear(newYear);
  }

  return (
    <div className="calendar-outer">
      <div className="calendar-header">
        <button onClick={() => handleMonthChange(-1)}>&lt;</button>
        <h2>{today.toLocaleString('de-DE', { month: 'long' })} {currentYear}</h2>
        <button onClick={() => handleMonthChange(1)}>&gt;</button>
      </div>
      <div className="calendar-toggle">
        <button
          className={!showPersonal ? 'active' : ''}
          onClick={() => setShowPersonal(false)}
        >Gemeinsamer Kalender</button>
        <button
          className={showPersonal ? 'active' : ''}
          onClick={() => setShowPersonal(true)}
        >Mein Kalender</button>
      </div>
      <form className="calendar-form" onSubmit={handleAddEvent}>
        <input
          type="text"
          placeholder="Titel"
          value={form.title}
          onChange={e => setForm(f => ({ ...f, title: e.target.value }))}
          required
        />
        <input
          type="date"
          value={form.date}
          onChange={e => setForm(f => ({ ...f, date: e.target.value }))}
          required
        />
        <input
          type="text"
          placeholder="Betroffene Personen (z.B. Jan, Mama)"
          value={form.persons}
          onChange={e => setForm(f => ({ ...f, persons: e.target.value }))}
          required
        />
        <button type="submit" disabled={saving}>{saving ? 'Speichern...' : 'Termin hinzufÃ¼gen'}</button>
      </form>
      <div className="calendar-grid">
        {WEEKDAYS.map(wd => <div key={wd} className="calendar-weekday">{wd}</div>)}
        {loading ? <div className="calendar-loading">Lade Termine...</div> : renderDays()}
      </div>
    </div>
  );
}
