import React, { useState } from 'react';
import './App.css';

const TABS = [
  { label: 'Kalender', key: 'calendar' },
  { label: 'Einkaufszettel', key: 'shopping' },
  { label: 'Hausarbeiten', key: 'chores' },
  { label: 'Admin', key: 'admin' },
];

function Login({ onLogin }) {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Dummy-Login, später durch echte Logik ersetzen
    onLogin({ name, isAdmin: name.toLowerCase() === 'admin' });
  };

  return (
    <div className="login-container">
      <h2>Familien App Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={e => setName(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Passwort"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

function TabNavigation({ tabs, activeTab, setActiveTab }) {
  return (
    <div className="tab-nav">
      {tabs.map(tab => (
        <button
          key={tab.key}
          className={activeTab === tab.key ? 'active' : ''}
          onClick={() => setActiveTab(tab.key)}
        >
          {tab.label}
        </button>
      ))}
    </div>
  );
}

// Kalender-Komponente
function Calendar({ user }) {
  const [events, setEvents] = useState([
    // Beispieltermin
    { title: 'Familienessen', date: '2024-06-10', persons: ['Mama', 'Papa', 'Jan'] },
  ]);
  const [title, setTitle] = useState('');
  const [date, setDate] = useState('');
  const [persons, setPersons] = useState('');

  const handleAddEvent = (e) => {
    e.preventDefault();
    if (!title || !date || !persons) return;
    setEvents([
      ...events,
      { title, date, persons: persons.split(',').map(p => p.trim()) }
    ]);
    setTitle('');
    setDate('');
    setPersons('');
  };

  // Filter für persönliche Termine
  const myEvents = events.filter(ev => ev.persons.includes(user.name) || ev.persons.length === 0);

  return (
    <div>
      <h2>Familienkalender</h2>
      <form onSubmit={handleAddEvent} className="calendar-form">
        <input
          type="text"
          placeholder="Titel"
          value={title}
          onChange={e => setTitle(e.target.value)}
          required
        />
        <input
          type="date"
          value={date}
          onChange={e => setDate(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Betroffene Personen (z.B. Jan, Mama)"
          value={persons}
          onChange={e => setPersons(e.target.value)}
          required
        />
        <button type="submit">Termin hinzufügen</button>
      </form>
      <h3>Alle Termine</h3>
      <ul className="calendar-list">
        {events.map((ev, idx) => (
          <li key={idx}>
            <strong>{ev.title}</strong> am {ev.date} <br />
            <span>Betroffen: {ev.persons.join(', ')}</span>
          </li>
        ))}
      </ul>
      <h3>Meine Termine</h3>
      <ul className="calendar-list">
        {myEvents.length === 0 && <li>Keine persönlichen Termine</li>}
        {myEvents.map((ev, idx) => (
          <li key={idx}>
            <strong>{ev.title}</strong> am {ev.date}
          </li>
        ))}
      </ul>
    </div>
  );
}

function App() {
  const [user, setUser] = useState(null);
  const [activeTab, setActiveTab] = useState(TABS[0].key);

  if (!user) {
    return <Login onLogin={setUser} />;
  }

  return (
    <div className="app-container">
      <header>
        <h1>Familien App</h1>
        <span>Willkommen, {user.name}</span>
      </header>
      <TabNavigation tabs={TABS} activeTab={activeTab} setActiveTab={setActiveTab} />
      <main>
        {activeTab === 'calendar' && <Calendar user={user} />}
        {activeTab === 'shopping' && <div>Einkaufszettel (in Entwicklung)</div>}
        {activeTab === 'chores' && <div>Hausarbeiten (in Entwicklung)</div>}
        {activeTab === 'admin' && user.isAdmin && <div>Admin-Bereich (in Entwicklung)</div>}
        {activeTab === 'admin' && !user.isAdmin && <div>Kein Zugriff</div>}
      </main>
    </div>
  );
}

export default App;
