import React, { useEffect, useState, useCallback } from 'react';
import { supabase } from './supabaseClient';
import './Feedback.css';

export default function Feedback({ user }) {
  const [feedbackList, setFeedbackList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({ content: '' });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState('');

  const isAdmin = user.is_admin;

  const fetchFeedback = useCallback(async () => {
    if (!isAdmin) return; // Nur Admins d端rfen Feedback lesen
    setLoading(true);
    const { data, error } = await supabase
      .from('feedback')
      .select('*')
      .order('created_at', { ascending: false });

    if (error) {
      setError('Fehler beim Laden des Feedbacks.');
      setFeedbackList([]);
    } else {
      setFeedbackList(data);
    }
    setLoading(false);
  }, [isAdmin]);

  useEffect(() => {
    if (isAdmin) {
      fetchFeedback();
    }
  }, [isAdmin, fetchFeedback]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmitFeedback = async (e) => {
    e.preventDefault();
    if (!form.content) return;
    setError(null);
    setSuccess('');

    const { error } = await supabase.from('feedback').insert([
      {
        content: form.content,
        submitted_by: user.name,
      }
    ]);

    if (error) {
      setError('Fehler beim Senden des Feedbacks.');
    } else {
      setForm({ content: '' });
      setSuccess('Vielen Dank f端r dein Feedback!');
    }
  };

  // Admin-Ansicht
  if (isAdmin) {
    return (
      <div className="feedback-container admin-view">
        <h2>Eingegangenes Feedback</h2>
        {loading && <p>Lade Feedback...</p>}
        {error && <p className="error-message">{error}</p>}
        <div className="feedback-list">
          {feedbackList.length === 0 && !loading && <p>Kein Feedback vorhanden.</p>}
          {feedbackList.map(item => (
            <div key={item.id} className="feedback-item">
              <p>"{item.content}"</p>
              <div className="feedback-meta">
                <span>Von: <strong>{item.submitted_by}</strong></span>
                <span>am {new Date(item.created_at).toLocaleString('de-DE')}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // Benutzer-Ansicht
  return (
    <div className="feedback-container user-view">
      <h2>Gib uns Feedback!</h2>
      <p>Hast du eine Idee oder einen Verbesserungsvorschlag? Lass es uns wissen!</p>
      <form onSubmit={handleSubmitFeedback} className="feedback-form">
        <textarea
          name="content"
          placeholder="Deine Idee..."
          value={form.content}
          onChange={handleInputChange}
          required
        />
        <button type="submit">Feedback abschicken</button>
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}
      </form>
    </div>
  );
}
