import React, { useState, useEffect } from 'react';
import './App.css';
import Auth from './Auth';
import Calendar from './Calendar';
import ShoppingList from './ShoppingList';

const TABS_BASE = [
  { label: 'Kalender', key: 'calendar' },
  { label: 'Einkaufszettel', key: 'shopping' },
  { label: 'Hausarbeiten', key: 'chores' },
];

const ADMIN_TAB = { label: 'Admin', key: 'admin' };

function TabNavigation({ tabs, activeTab, setActiveTab }) {
  return (
    <div className="tab-nav">
      {tabs.map(tab => (
        <button
          key={tab.key}
          className={activeTab === tab.key ? 'active' : ''}
          onClick={() => setActiveTab(tab.key)}
        >
          {tab.label}
        </button>
      ))}
    </div>
  );
}

function App() {
  const [user, setUser] = useState(null);
  const [activeTab, setActiveTab] = useState(TABS_BASE[0].key);

  // Beim Start der App prüfen, ob ein Benutzer im Local Storage ist
  useEffect(() => {
    const storedUser = localStorage.getItem('familien_app_user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const handleAuthSuccess = (authenticatedUser) => {
    localStorage.setItem('familien_app_user', JSON.stringify(authenticatedUser));
    setUser(authenticatedUser);
  };

  const handleLogout = () => {
    localStorage.removeItem('familien_app_user');
    setUser(null);
  };

  // Tabs basierend auf Admin-Status anpassen
  const availableTabs = user && user.is_admin ? [...TABS_BASE, ADMIN_TAB] : TABS_BASE;

  if (!user) {
    return <Auth onAuthSuccess={handleAuthSuccess} />;
  }

  return (
    <div className="app-container">
      <header>
        <h1>Familien App</h1>
        <div className="header-user-info">
          <span>Willkommen, {user.name}</span>
          <button onClick={handleLogout} className="logout-button">Logout</button>
        </div>
      </header>
      <TabNavigation tabs={availableTabs} activeTab={activeTab} setActiveTab={setActiveTab} />
      <main>
        {activeTab === 'calendar' && <Calendar user={user} />}
        {activeTab === 'shopping' && <ShoppingList user={user} />}
        {activeTab === 'chores' && <div>Hausarbeiten (in Entwicklung)</div>}
        {activeTab === 'admin' && user.is_admin && <div>Admin-Bereich (in Entwicklung)</div>}
      </main>
    </div>
  );
}

export default App;
